import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import styles from "./allstore.module.css";
import filter from "../images/filter.png";
import search from "../images/search.png";
import { Shoedata } from "./Shoedata";
import Cart from "./Cart";
import Storeshoe from "./Storeshoe";
import { Cartdata } from "./Cartdata";

const Allstore = () => {
  const [selected, setSelected] = useState("");
  const [cselected, setcSelected] = useState("");
  const [dselected, setdSelected] = useState("");
  const [tselected, settSelected] = useState("");

  const [price, setprice] = useState();
  const [color, setcolor] = useState();
  const [type, settype] = useState();
  const [design, setdesign] = useState();

  const handleChange = (e) => {
    setSelected(e.target.value);
  };
  const colorchange = (e) => {
    setcSelected(e.target.value);
  };
  const designChange = (e) => {
    setdSelected(e.target.value);
  };
  const typeChange = (e) => {
    settSelected(e.target.value);
  };
  const [shoesid, setshoesid] = useState("");
  const [show, setshow] = useState(0);

  const showfun = (e) => {
    setshow(1);
    setshoesid(e.id);
  };

  const removecart = (id) => {
    console.log("remove cart");
    setDatacart(datacart.filter((item) => item.id !== id));
    console.log(datacart);
  };

  const [datacart, setDatacart] = useState(Cartdata);

  const addtocart = (e) => {
    if (!datacart.find((i) => i.id === e.id)) {
      setDatacart([...datacart, e]);
      console.log(datacart);
    }
  };

  return (
    <>
      {show ? (
        <Storeshoe
          datacart={datacart}
          setDatacart={setDatacart}
          idm={shoesid}
          setshow={setshow}
          removecart={removecart}
        />
      ) : (
        <div>
          <div className={styles.all}>
            <div className={styles.filterdiv}>
              <div className={styles.filterdivheader}>
                <div>
                  <p>FILTERS</p>
                </div>
                <div>
                  <p>
                    <img src={filter} alt=""></img>
                  </p>
                </div>
              </div>
              <div className={styles.remaining}>
                <p>Cost</p>
                <div>
                  <div>
                    <input
                      type="checkbox"
                      value="price1"
                      checked={selected === "price1"}
                      onChange={handleChange}
                    />
                    <p>Rs. 1500-4000</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="price2"
                      checked={selected === "price2"}
                      onChange={handleChange}
                    />
                    <p>Rs. 1500-4000</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="price3"
                      checked={selected === "price3"}
                      onChange={handleChange}
                    />
                    <p>Rs. 1500-4000</p>
                  </div>
                </div>
                <p>Colour</p>
                <div className="color">
                  <input
                    type="checkbox"
                    value="color1"
                    checked={cselected === "color1"}
                    onChange={colorchange}
                  />
                  <input
                    type="checkbox"
                    value="color2"
                    checked={cselected === "color2"}
                    onChange={colorchange}
                  />
                  <input
                    type="checkbox"
                    value="color3"
                    checked={cselected === "color3"}
                    onChange={colorchange}
                  />
                  <input
                    type="checkbox"
                    value="color4"
                    checked={cselected === "color4"}
                    onChange={colorchange}
                  />
                  <input
                    type="checkbox"
                    value="color5"
                    checked={cselected === "color5"}
                    onChange={colorchange}
                  />
                </div>
                <p>Design Templates</p>
                <div>
                <div>
                    <input
                      type="checkbox"
                      value="price1"
                      checked={dselected === "price1"}
                      onChange={designChange}
                    />
                    <p>2</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="price1"
                      checked={dselected === "price1"}
                      onChange={designChange}
                    />
                    <p>3</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="price1"
                      checked={dselected === "price1"}
                      onChange={designChange}
                    />
                    <p>3+</p>
                  </div>
                </div>
                <p>Type</p>
                <div>
                <div>
                    <input
                      type="checkbox"
                      value="type1"
                      checked={tselected === "type1"}
                      onChange={typeChange}
                    />
                    <p>Loafers</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="type1"
                      checked={tselected === "type1"}
                      onChange={typeChange}
                    />
                    <p>Sneakers</p>
                  </div>
                </div>
              </div>
              <div className={styles.filterbutton}>
                <button>apply</button>
              </div>
            </div>
            <div className={styles.shoediv}>
              <div className={styles.shoedivheader}>
                <div>
                  <p>SHOES</p>
                </div>
                <div>
                  <p>
                    <img src={search} alt=""></img>
                  </p>
                  <button>sort by</button>
                </div>
              </div>
              <div className={styles.shoedivbody}>
                {Shoedata?.map((e) => {
                  return (
                    <button onClick={() => showfun(e)}>
                      <div className={styles.shoecard}>
                        <img src={e.url} alt=""></img>
                        <p>{e.name}</p>
                        <div>
                          <p>{e.price}</p>
                          <p>
                            <span style={{ color: "orange" }}>
                              &#9734;&#9734;&#9734;&#9734;&#9734;
                            </span>
                          </p>
                        </div>
                      </div>
                    </button>
                  );
                })}
              </div>
            </div>
            {/* <div className={styles.cartdiv}>
          <div className={styles.filterdivheader}>
            <div>
              <p>CART</p>
            </div>
            <div>
              <p>
                <img src={cart} alt=""></img>
              </p>
            </div>
          </div>
          <div>What's stopping you, designer?</div>
          <div>
            <button>order now</button>
          </div>
        </div> */}
            <Cart datacart={datacart} removecart={removecart}></Cart>
          </div>
        </div>
      )}
    </>
  );
};

export default Allstore;
