import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import styles from "./allstore.module.css";
import filter from "../images/filter.png";
import search from "../images/search.png";
import { Shoedata } from "./Shoedata";
import Cart from "./Cart";
import Storeshoe from "./Storeshoe";
import { Cartdata } from "./Cartdata";

const Allstore = () => {
  const [selected, setSelected] = useState("");
  const [cselected, setcSelected] = useState("");
  const [dselected, setdSelected] = useState("");
  const [tselected, settSelected] = useState("");

  const [price, setprice] = useState(0);
  const [color, setcolor] = useState("");
  const [design, setdesign] = useState("");
  const [type, settype] = useState("");

  const apply=()=>{
    setprice=price;
    setcolor=color;
    setdesign=design;
    settype=type;
  } 

  console.log(price);

  const handleChange = async(e) => {
    setSelected(e.target.value);

    if(selected=="price1"){setprice(1500)}
    else if(selected=="price2"){setprice(4001)}
    else if(selected=="price3"){setprice(7001)}
  };
  const colorchange = (e) => {
    setcSelected(e.target.value);
      if(cselected=="color1"){setcolor("red")}
      else if(cselected=="color2"){setcolor("blue")}
      else if(cselected=="color3"){setcolor("yellow")}
      else if(cselected=="color3"){setcolor("green")}
      else if(cselected=="color3"){setcolor("gold")}
  };
  const designChange = (e) => {
    setdSelected(e.target.value);
    if(dselected=="design1"){setdesign(2)}
    else if(dselected=="design2"){setdesign(3)}
    else if(dselected=="design3"){setdesign(4)}
  };
  const typeChange = (e) => {
    settSelected(e.target.value);
    if(tselected=="type1"){settype("loafer")}
    else if(tselected=="type2"){settype("sneaker")}
  };


  const [shoesid, setshoesid] = useState("");
  const [show, setshow] = useState(0);

  const showfun = (e) => {
    setshow(1);
    setshoesid(e.id);
  };

  const removecart = (id) => {
    console.log("remove cart");
    setDatacart(datacart.filter((item) => item.id !== id));
    console.log(datacart);
  };

  const [datacart, setDatacart] = useState(Cartdata);

  const addtocart = (e) => {
    if (!datacart.find((i) => i.id === e.id)) {
      setDatacart([...datacart, e]);
      console.log(datacart);
    }
  };

  return (
    <>
      {show ? (
        <Storeshoe
          datacart={datacart}
          setDatacart={setDatacart}
          idm={shoesid}
          setshow={setshow}
          removecart={removecart}
        />
      ) : (
        <div>
          <div className={styles.all}>
            <div className={styles.filterdiv}>
              <div className={styles.filterdivheader}>
                <div>
                  <p>FILTERS</p>
                </div>
                <div>
                  <p>
                    <img src={filter} alt=""></img>
                  </p>
                </div>
              </div>
              <div className={styles.remaining}>
                <p>Cost</p>
                <div>
                  <div>
                    <input
                      type="checkbox"
                      value="price1"
                      checked={selected === "price1"}
                      onChange={handleChange}
                    />
                    <p>Rs. 1500-4000</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="price2"
                      checked={selected === "price2"}
                      onChange={handleChange}
                    />
                    <p>Rs. 4001-7000</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="price3"
                      checked={selected === "price3"}
                      onChange={handleChange}
                    />
                    <p>Rs. 7001+</p>
                  </div>
                </div>
                <p>Colour</p>
                <div className="color">
                  <input
                    type="checkbox"
                    value="color1"
                    checked={cselected === "color1"}
                    onChange={colorchange}
                  />
                  <input
                    type="checkbox"
                    value="color2"
                    checked={cselected === "color2"}
                    onChange={colorchange}
                  />
                  <input
                    type="checkbox"
                    value="color3"
                    checked={cselected === "color3"}
                    onChange={colorchange}
                  />
                  <input
                    type="checkbox"
                    value="color4"
                    checked={cselected === "color4"}
                    onChange={colorchange}
                  />
                  <input
                    type="checkbox"
                    value="color5"
                    checked={cselected === "color5"}
                    onChange={colorchange}
                  />
                </div>
                <p>Design Templates</p>
                <div>
                <div>
                    <input
                      type="checkbox"
                      value="design1"
                      checked={dselected === "design1"}
                      onChange={designChange}
                    />
                    <p>2</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="design2"
                      checked={dselected === "design2"}
                      onChange={designChange}
                    />
                    <p>3</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="design3"
                      checked={dselected === "design3"}
                      onChange={designChange}
                    />
                    <p>3+</p>
                  </div>
                </div>
                <p>Type</p>
                <div>
                <div>
                    <input
                      type="checkbox"
                      value="type1"
                      checked={tselected === "type1"}
                      onChange={typeChange}
                    />
                    <p>Loafers</p>
                  </div>
                  <div>
                    <input
                      type="checkbox"
                      value="type2"
                      checked={tselected === "type2"}
                      onChange={typeChange}
                    />
                    <p>Sneakers</p>
                  </div>
                </div>
              </div>
              <div className={styles.filterbutton}>
                <button onClick={()=>apply(price,color,design,type)}>apply</button>
              </div>
            </div>
