import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import styles from "./allstore.module.css";
import filter from "../images/filter.png";
import search from "../images/search.png";
import { Shoedata } from "./Shoedata";
import Cart from "./Cart";
import Storeshoe from "./Storeshoe";
import { Cartdata } from "./Cartdata";

const Allstore = () => {
  const [selected, setSelected] = useState("");
  const [cselected, setcSelected] = useState("");
  const [dselected, setdSelected] = useState("");
  const [tselected, settSelected] = useState("");

  const [price, setprice] = useState(0);
  const [color, setcolor] = useState("");
  const [design, setdesign] = useState("");
  const [type, settype] = useState("");

  const [aprice, setaprice] = useState(0);
  const [acolor, setacolor] = useState("");
  const [adesign, setadesign] = useState();
  const [atype, setatype] = useState("");

  console.log(acolor);

  const apply = () => {
    setaprice(price);
    setacolor(color);
    console.log("jvgvvvgvggvvggvv", setacolor);
    setadesign(design);
    setatype(type);
  };

  useEffect(()=>{

  },[price,color])

  console.log(price);

  const handleChange = async (e) => {
    setSelected(e.target.value);

    if (selected == "price1") {
      setprice(1500);
    } else if (selected == "price2") {
      setprice(4001);
    } else if (selected == "price3") {
      setprice(7001);
    }
  };
  const colorchange = (e) => {
    setcSelected(e.target.value);
    if (cselected == "color1") {
      setcolor("red");
    } else if (cselected == "color2") {
      setcolor("blue");
    } else if (cselected == "color3") {
      setcolor("yellow");
    } else if (cselected == "color4") {
      setcolor("green");
    } else if (cselected == "color5") {
      setcolor("gold");
    }
  };
  const designChange = (e) => {
    setdSelected(e.target.value);
    if (dselected == "design1") {
      setdesign(2);
    } else if (dselected == "design2") {
      setdesign(3);
    } else if (dselected == "design3") {
      setdesign(4);
    }
  };
  const typeChange = (e) => {
    settSelected(e.target.value);
    if (tselected == "type1") {
      settype("loafer");
    } else if (tselected == "type2") {
      settype("sneaker");
    }
  };

  const [shoesid, setshoesid] = useState("");
  const [show, setshow] = useState(0);

  const showfun = (e) => {
    setshow(1);
    setshoesid(e.id);
  };

  const removecart = (id) => {
    console.log("remove cart");
    setDatacart(datacart.filter((item) => item.id !== id));
    console.log(datacart);
  };

  const [datacart, setDatacart] = useState(Cartdata);

  const addtocart = (e) => {
    if (!datacart.find((i) => i.id === e.id)) {
      setDatacart([...datacart, e]);
      console.log(datacart);
    }
  };

  ///////// sort
  const [sortMethod, setSortMethod] = useState("asc");
  const sortedProducts = [...Shoedata].sort((a, b) => {
    if (sortMethod === "asc") {
      return a.price - b.price;
    } else if (sortMethod === "") {
      return;
    } else {
      return b.price - a.price;
    }
  });

  ////////search
